import React, { useEffect, useState } from "react";
import coinChange from "../../service/coinChange";
import "./styles.css"

export default function Board() {
  const [result, setResult] = useState(null);

  const generateGraphs = async (coins, amount) => {
    try {
      const res = await coinChange(coins, amount)
      console.log(res);
      setResult(res)
    } catch (error) {
      alert("Não foi possível gerar o troco, verifique se preencheu corretamente!")
    }
  }

  const handleSubmit = () => {
    try {    
      var input = document.querySelector("#nos");
      var number = input.value;
      var input2 = document.querySelector("#conexoes");
      var texto2 = input2.value;
      const res = texto2.split("\n");
      let arr = []
      console.log(res);
      for (let i of res){
        i = String(i);
        console.log(i);
        let split = i.split(",")
        if (split[0] === split[1] || (split[0] > number-1) || (split[1] > number-1)){
          throw new Error({ code : 403, message : "Exception" });
        }
        else 
          arr.push([ parseInt(split[0]), parseInt(split[1]) ])
      }

      const sendObj = {
        nodes_number: number,
        edges: arr
      }
      generateGraphs(sendObj)
    } catch (error) {
      alert("Não foi possível gerar as conexões, verifique se preencheu corretamente!")
    }
  }

  const onReset = () => {
    setResult(null)
  }

  return (
    <div className='board'>
          {result ? 
          <>
            <span className='board-span'>{result}</span>
            <button onClick={onReset} className='board-button-2'>Tentar novamente!</button>
          </>
          :
            <div>
              <div className='board-span-input'>
                <span className='board-span'>Quantia em dinheiro:</span>
                <input id="amount" className='board-input-1' placeholder='4'
                />
              </div>
              <div className='board-span-input'>
                <span className='board-span'>Valores das moedas:</span>
                <textarea id="coins" className='board-text-area' cols="2" placeholder="1&#10;10&#10;25&#10;50"></textarea>
              </div>
            <button onClick={handleSubmit} type='submit' className='board-button'>Receber troco!</button>
            </div>
          }
    </div>
  );
}
